CREATE OR REPLACE FUNCTION pls_chamado_pck.get_ds_etapa ( nr_seq_etapa_p pls_tipo_chamado_etapa.nr_sequencia%type, ie_nr_etapa_p text) RETURNS varchar AS $body$
DECLARE

ds_etapa_w	pls_tipo_chamado_etapa.ds_etapa%type;
nr_ordem_w	pls_tipo_chamado_etapa.nr_ordem%type;

BEGIN
select	nr_ordem,
	ds_etapa
into STRICT	nr_ordem_w,
	ds_etapa_w
from	pls_tipo_chamado_etapa
where	nr_sequencia	= nr_seq_etapa_p;

if (ie_nr_etapa_p = 'S') then
	return nr_ordem_w || ' - ' || ds_etapa_w;
else
	return ds_etapa_w;
end if;
END;

$body$
