CREATE OR REPLACE FUNCTION pls_chamado_pck.get_prioridade_pendencia ( nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

dt_prazo_w	pls_chamado_etapa.dt_prazo%type;
dt_liberacao_w	pls_chamado_etapa.dt_liberacao%type;
ds_retorno_w	varchar(1);

BEGIN
select	max(a.dt_prazo),
	max(a.dt_liberacao)
into STRICT	dt_prazo_w,
	dt_liberacao_w
from	pls_chamado_etapa a
where	a.nr_sequencia = nr_seq_chamado_etapa_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	if (dt_prazo_w < dt_liberacao_w) then
		return '3'; --Atrasada
	else
		return '1'; --Em dia
	end if;
else
	if (dt_prazo_w < clock_timestamp()) then
		return '3'; --Atrasada
	elsif (dt_prazo_w-2 < clock_timestamp()) then
		return '2'; --Proximo do prazo
	else
		return '1'; --Em dia
	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
