CREATE OR REPLACE PROCEDURE pls_chamado_pck.liberar_etapa ( nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

qt_etapa_hist_w bigint;
nr_seq_proxima_etapa_w pls_chamado_etapa.nr_seq_proxima_etapa%type;

BEGIN

select	count(nr_sequencia)
into STRICT	qt_etapa_hist_w
from	pls_chamado_etapa_hist
where	nr_seq_chamado_etapa	= nr_seq_chamado_etapa_p
and	ie_tipo_historico	= 'U'
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_etapa_hist_w > 0) then
	update	pls_chamado_etapa
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_chamado_etapa_p;
	
	CALL pls_chamado_pck.enviar_parecer_chamado(	nr_seq_chamado_etapa_p,
					nm_usuario_p);
	
	select	nr_seq_proxima_etapa
	into STRICT	nr_seq_proxima_etapa_w
	from	pls_chamado_etapa
	where	nr_sequencia = nr_seq_chamado_etapa_p;
	
	if (nr_seq_proxima_etapa_w IS NOT NULL AND nr_seq_proxima_etapa_w::text <> '') then
		CALL pls_chamado_pck.gerar_proxima_etapa(		null,
						null,
						nr_seq_chamado_etapa_p,
						nm_usuario_p,
						'S');
	end if;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1192606);
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
