CREATE OR REPLACE PROCEDURE pls_comissao_equipe_pck.excluir_registro_nulo ( nr_seq_lote_p pls_lote_comissao.nr_sequencia%type) AS $body$
DECLARE


current_setting('pls_comissao_equipe_pck.c01')::CURSOR CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_comissao
	from	pls_comissao a
	where	a.nr_seq_lote = nr_seq_lote_p
	and	not exists (	SELECT	1
				from	pls_comissao_beneficiario x
				where	a.nr_sequencia = x.nr_seq_comissao);

BEGIN

delete from pls_comissao_beneficiario
where nr_sequencia in (	SELECT	a.nr_sequencia
			from	pls_comissao_beneficiario a,
				pls_comissao b
			where	b.nr_sequencia = a.nr_seq_comissao
			and	b.nr_seq_lote = nr_seq_lote_p
			and	not exists (	select	1
						from	pls_comissao_benef_item x
						where	a.nr_sequencia = x.nr_seq_comissao_benef));
		
for r_c01_w in current_setting('pls_comissao_equipe_pck.c01')::loop CURSOR
	begin	
	update	pls_vendedor_lanc
	set	nr_seq_comissao  = NULL
	where	nr_seq_comissao = r_c01_w.nr_seq_comissao;	

	delete	from pls_vendedor_lanc a
	where	exists (	SELECT	1
			from	pls_repasse_lanc x
			where	x.nr_sequencia 	  = a.nr_seq_repasse_lanc
			and	x.nr_seq_comissao = r_c01_w.nr_seq_comissao);					

	delete	from pls_repasse_lanc
	where	nr_seq_comissao	= r_c01_w.nr_seq_comissao;	
				
	delete from pls_comissao
	where 	nr_sequencia = r_c01_w.nr_seq_comissao;
	end;
end loop;	

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
