CREATE OR REPLACE PROCEDURE pls_comissao_equipe_pck.valida_vendedor ( nr_seq_canal_venda_p pls_vendedor.nr_sequencia%type, dt_referencia_p pls_lote_comissao.dt_referencia%type) AS $body$
DECLARE


qt_equipe_w	bigint;


BEGIN

if (nr_seq_canal_venda_p IS NOT NULL AND nr_seq_canal_venda_p::text <> '') then
	select	count(1)
	into STRICT	qt_equipe_w
	from	pls_equipe_vend_vinculo	a,
		pls_equipe_vendedor	b
	where	a.nr_seq_equipe	= b.nr_sequencia
	and	a.nr_seq_vendedor = nr_seq_canal_venda_p
	and	dt_referencia_p between coalesce(a.dt_inicio_vigencia,dt_referencia_p) and coalesce(a.dt_fim_vigencia,dt_referencia_p);
	
	if (qt_equipe_w = 0)then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(224196);
	else
		select	count(*)
		into STRICT	qt_equipe_w
		from	pls_equipe_vend_vinculo	a,
			pls_equipe_vendedor	b
		where	a.nr_seq_equipe	= b.nr_sequencia
		and	a.nr_seq_vendedor = nr_seq_canal_venda_p			
		and	dt_referencia_p between coalesce(a.dt_inicio_vigencia,dt_referencia_p) and coalesce(a.dt_fim_vigencia,dt_referencia_p);
		
		if (qt_equipe_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(224197);
		end if;				
	end if;	
end if;

END;

$body$
