CREATE OR REPLACE PROCEDURE pls_compartilhamento_risco_pck.carregar_municipios_atuacao ( nr_seq_operadora_p pls_congenere.nr_sequencia%type) AS $body$
DECLARE


current_setting('pls_compartilhamento_risco_pck.c01')::CURSOR( CURSOR(	nr_seq_operadora_pc	pls_congenere.nr_sequencia%type) FOR
	SELECT	cd_municipio_ibge
	from	pls_regiao_atuacao
	where	(cd_municipio_ibge IS NOT NULL AND cd_municipio_ibge::text <> '')
	and	nr_seq_operadora = nr_seq_operadora_pc
	
union

	SELECT	c.cd_municipio_ibge
	from	pls_regiao_atuacao a,
		pls_regiao b,
		pls_regiao_local c
	where	b.nr_sequencia = a.nr_seq_regiao
	and	b.nr_sequencia = c.nr_seq_regiao
	and	coalesce(a.cd_municipio_ibge::text, '') = ''
	and	a.nr_seq_operadora = nr_seq_operadora_pc
	
union

	select	b.cd_municipio_ibge
	from	pls_regiao_atuacao a,
		sus_municipio b
	where	a.sg_uf_municipio = b.ds_unidade_federacao
	and	coalesce(a.cd_municipio_ibge::text, '') = ''
	and	coalesce(a.nr_seq_regiao::text, '') = ''
	and	a.nr_seq_operadora = nr_seq_operadora_pc;

BEGIN

for r_c01_w in current_setting('pls_compartilhamento_risco_pck.c01')::CURSOR( (nr_seq_operadora_p) loop
	begin
	current_setting('pls_compartilhamento_risco_pck.tb_cd_municipio_ibge_w')::pls_util_cta_pck.t_varchar2_table_10(current_setting('pls_compartilhamento_risco_pck.indice_municipio_w')::integer) := r_c01_w.cd_municipio_ibge;
	PERFORM set_config('pls_compartilhamento_risco_pck.indice_municipio_w', current_setting('pls_compartilhamento_risco_pck.indice_municipio_w')::integer + 1, false);
	end;
end loop;

END;

$body$
