CREATE OR REPLACE PROCEDURE pls_compartilhamento_risco_pck.desfazer_confirmacao_lote ( nr_seq_lote_copart_risco_p pls_lote_compart_risco.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


current_setting('pls_compartilhamento_risco_pck.c01')::CURSOR( CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_comp_risco,
		b.nr_sequencia nr_seq_seg_repasse
	from	pls_compartilhamento_risco 	a,
		pls_segurado_repasse		b
	where	a.nr_sequencia 	= b.nr_seq_comp_risco
	and	a.nr_seq_lote	= nr_seq_lote_copart_risco_p;
	
ds_erro_w			varchar(255);
tb_nr_seq_comp_risco_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_seg_repasse_w		pls_util_cta_pck.t_number_table;

