fetch current_setting('pls_compartilhamento_risco_pck.c01')::CURSOR( bulk collect into tb_nr_seq_comp_risco_w, tb_nr_seq_seg_repasse_w;
exit when tb_nr_seq_comp_risco_w.count = 0;
	begin
	
	for i in tb_nr_seq_comp_risco_w.first..tb_nr_seq_comp_risco_w.last loop
		
		pls_desfazer_repasse_segurado(tb_nr_seq_seg_repasse_w(i), 'N', nm_usuario_p, ds_erro_w);
		
