		delete
		from 	pls_segurado_repasse 
		where 	nr_sequencia 	= tb_nr_seq_seg_repasse_w(i);
		
		update	pls_compartilhamento_risco
		set	ie_status	 = NULL,
			nm_usuario 	= nm_usuario_p,
			dt_atualizacao 	= clock_timestamp()
		where 	nr_sequencia 	= tb_nr_seq_comp_risco_w(i);
		
	end loop;
	end;
end loop;
close current_setting('pls_compartilhamento_risco_pck.c01')::CURSOR(;

