set	dt_confirmacao 	 = NULL,
	ie_status	= '2',
	nm_usuario 	= nm_usuario_p,
	dt_atualizacao 	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_copart_risco_p;

