SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_compartilhamento_risco_pck.inserir_segurado_repasse ( ie_descarregar_vetor_p text) AS $body$
BEGIN

if	((indice_comp_w >= pls_util_pck.qt_registro_transacao_w) or (ie_descarregar_vetor_p = 'S')) then
	if (tb_nr_seq_segurado_comp_w.count > 0) then
		forall i in tb_nr_seq_segurado_comp_w.first..tb_nr_seq_segurado_comp_w.last
			insert into pls_segurado_repasse(nr_sequencia, cd_estabelecimento, dt_atualizacao,
				dt_atualizacao_nrec, nr_seq_segurado, nr_seq_congenere,
				ie_tipo_compartilhamento, nm_usuario, nm_usuario_nrec,
				ie_tipo_repasse, nr_seq_comp_risco, dt_repasse,
				nr_seq_congenere_atend, dt_fim_repasse)
			values (nextval('pls_segurado_repasse_seq'), cd_estabelecimento_p, clock_timestamp(),
				clock_timestamp(), tb_nr_seq_segurado_comp_w(i), current_setting('pls_compartilhamento_risco_pck.tb_nr_seq_operadora_destino_w')::pls_util_cta_pck.t_number_table(i),
				2, nm_usuario_p, nm_usuario_p,
				'C', tb_nr_seq_comp_risco_w(i), current_setting('pls_compartilhamento_risco_pck.pls_lote_compart_risco_w')::pls_lote_compart_risco%rowtype.dt_referencia,
				current_setting('pls_compartilhamento_risco_pck.tb_nr_seq_operadora_destino_w')::pls_util_cta_pck.t_number_table(i), tb_dt_rescisao_w(i))
			RETURNING nr_sequencia, nr_seq_comp_risco
			BULK COLLECT INTO tb_nr_seq_segurado_repasse_w, tb_nr_seq_comp_risco_ww;
		commit;
		
		if (tb_nr_seq_segurado_repasse_w.count > 0) then
			for i in tb_nr_seq_segurado_repasse_w.first .. tb_nr_seq_segurado_repasse_w.last loop
				begin
					CALL pls_liberar_repasse_seg(tb_nr_seq_segurado_repasse_w(i), nm_usuario_p, 'N');
					
					update	pls_compartilhamento_risco
					set	ie_status = 'C'
					where	nr_sequencia = tb_nr_seq_comp_risco_ww(i);
				exception
				when others then
					update	pls_compartilhamento_risco
					set	ie_status = 'E'
					where	nr_sequencia = tb_nr_seq_comp_risco_ww(i);
				end;
			end loop;
		end if;
	end if;
	
	CALL pls_compartilhamento_risco_pck.limpar_vetor_confirmar();
else
	indice_comp_w	:= indice_comp_w + 1;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
