CREATE OR REPLACE FUNCTION pls_compartilhamento_risco_pck.verif_benef_repas_operadora ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_cooperativa_p pls_segurado_repasse.nr_seq_congenere%type, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_repasse_operadora_w		bigint;


BEGIN

qt_repasse_operadora_w	:= 0;

if (nr_seq_cooperativa_p IS NOT NULL AND nr_seq_cooperativa_p::text <> '') then
	select	count(1)
	into STRICT	qt_repasse_operadora_w
	from	pls_segurado_repasse
	where	nr_seq_segurado = nr_seq_segurado_p
	and	nr_seq_congenere = nr_seq_cooperativa_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and     dt_referencia_p between dt_repasse and fim_dia(coalesce(dt_fim_repasse, dt_referencia_p));
end if;

return	qt_repasse_operadora_w;

END;

$body$
