SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_consistir_diops_pck.pls_carregar_info_diops ( nr_seq_periodo_p bigint, cd_estabelecimento_p bigint) AS $body$
BEGIN

	select	max(dt_periodo_inicial),
		max(dt_periodo_final)
	into STRICT	current_setting('pls_consistir_diops_pck.dt_periodo_inicial_w')::diops_periodo.dt_periodo_inicial%type,
		current_setting('pls_consistir_diops_pck.dt_periodo_final_w')::diops_periodo.dt_periodo_final%type
	from	diops_periodo
	where	nr_sequencia	= nr_seq_periodo_p;

	if (current_setting('pls_consistir_diops_pck.dt_periodo_inicial_w')::diops_periodo.dt_periodo_inicial%type >= to_date('01/01/2019', 'dd/mm/yyyy')) then
		PERFORM set_config('pls_consistir_diops_pck.ie_2019_ou_posterior_w', 'S', false);
	else
		PERFORM set_config('pls_consistir_diops_pck.ie_2019_ou_posterior_w', 'N', false);
	end if;

	PERFORM set_config('pls_consistir_diops_pck.nm_usuario_w', coalesce(Obter_Usuario_Ativo,'Tasy'), false);

	PERFORM set_config('pls_consistir_diops_pck.cd_empresa_w', obter_empresa_estab(cd_estabelecimento_p), false);

	select	coalesce(max(nr_sequencia),0)
	into STRICT	current_setting('pls_consistir_diops_pck.nr_seq_movimento_w')::bigint
	from	diops_periodo_inconsist a
	where	a.nr_seq_periodo	= nr_seq_periodo_p;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	current_setting('pls_consistir_diops_pck.nr_seq_transacao_w')::diops_transacao.nr_sequencia%type
	from	diops_transacao
	where	ie_tipo_transacao	= 'F'
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	dt_vigencia		= (SELECT	max(dt_vigencia)
					  from	diops_transacao
					  where	ie_tipo_transacao = 'F'
					  and	cd_estabelecimento	= cd_estabelecimento_p
					  and	dt_vigencia		<= current_setting('pls_consistir_diops_pck.dt_periodo_inicial_w')::diops_periodo.dt_periodo_inicial%type);

	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_diops_pck.pls_carregar_info_diops ( nr_seq_periodo_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;
