SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_consistir_diops_pck.pls_consist_fin_sinistro ( nr_seq_periodo_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE

	
	ie_evento_30_60_w	diops_fin_regra_sinistro.ie_evento_30_60%type;
	vl_evento_total_w	diops_fin_sinistro.vl_evento_ate_30%type;
	vl_saldo_final_w	diops_fin_mov_passivo.vl_saldo_final%type;
	
	
BEGIN
	
	delete	
	from 	diops_periodo_inconsist a
	where	a.nr_seq_periodo	= nr_seq_periodo_p
	and	a.nr_seq_inconsistencia	= 61;
	
	commit;
	
	select	max(a.ie_evento_30_60)
	into STRICT	ie_evento_30_60_w
	from	diops_fin_regra_sinistro a
	where	current_setting('pls_consistir_diops_pck.dt_periodo_inicial_w')::diops_periodo.dt_periodo_inicial%type	>= a.dt_inicio_vigencia
	and	current_setting('pls_consistir_diops_pck.dt_periodo_final_w')::diops_periodo.dt_periodo_final%type	<= coalesce(a.dt_fim_vigencia,clock_timestamp());

	select	max(CASE WHEN ie_evento_30_60_w=30 THEN coalesce(a.vl_evento_ate_30,0) + coalesce(a.vl_evento_pos_30,0) + coalesce(a.vl_evento_ate_30_cor, 0) + coalesce(a.vl_evento_pos_30_cor, 0)  ELSE coalesce(a.vl_evento_ate_60,0) + coalesce(a.vl_evento_pos_60,0) + coalesce(a.vl_evento_ate_60_cor, 0) + coalesce(a.vl_evento_pos_60_cor, 0) END )
	into STRICT	vl_evento_total_w	
	from	diops_fin_sinistro a
	where	a.nr_seq_periodo	= nr_seq_periodo_p;
	
	select	coalesce(sum(a.vl_saldo_final),0) vl_saldo_final
	into STRICT	vl_saldo_final_w
	from 	diops_fin_mov_passivo	a
	where	a.nr_seq_periodo	= nr_seq_periodo_p
	and	a.ds_conta	in (	'21111102', '21111202', '21112102', '21112202', '21111103', '21111203', '21112103', '21112203',
					'23111102', '23111202', '23112102', '23112202', '23111103', '23111203', '23112103', '23112203');
	
	-- Requirement 06: Sum of the values must be equals to the balance of PEL 
	if (vl_evento_total_w <> vl_saldo_final_w) then
		PERFORM set_config('pls_consistir_diops_pck.nr_quadro_w', 30, false);	
		
		if (ie_evento_30_60_w = 30) then
			PERFORM set_config('pls_consistir_diops_pck.nm_campo_ate_macro_w', '30', false);
			PERFORM set_config('pls_consistir_diops_pck.nm_campo_pos_macro_w', '60', false);
			CALL pls_consistir_diops_pck.pls_gravar_consistencia(nr_seq_periodo_p, cd_estabelecimento_p, 61);
		else
			PERFORM set_config('pls_consistir_diops_pck.nm_campo_ate_macro_w', '60', false);
			PERFORM set_config('pls_consistir_diops_pck.nm_campo_pos_macro_w', '30', false);
			CALL pls_consistir_diops_pck.pls_gravar_consistencia(nr_seq_periodo_p, cd_estabelecimento_p, 61);
		end if;		
	end if;
	
	PERFORM set_config('pls_consistir_diops_pck.nr_quadro_w', 0, false);	
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_diops_pck.pls_consist_fin_sinistro ( nr_seq_periodo_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;
