CREATE OR REPLACE FUNCTION pls_conta_autor_pck.pls_obter_utiliza_guia_mat ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_guia_mat_p pls_guia_plano_mat.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_autorizada_w		pls_conta_mat.qt_material%type;
qt_utilizada_w		pls_conta_mat.qt_material%type;
qt_saldo_w		pls_conta_mat.qt_material%type;
nr_seq_material_w	pls_conta_mat.nr_seq_material%type;


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') and (nr_seq_guia_mat_p IS NOT NULL AND nr_seq_guia_mat_p::text <> '') then

	select	max(nr_seq_material),
		max(qt_autorizada)
	into STRICT	nr_seq_material_w,
		qt_autorizada_w
	from	pls_guia_plano_mat
	where	nr_sequencia = nr_seq_guia_mat_p;
	
	select 	coalesce(sum(qt_saldo), 0),
		coalesce(sum(qt_utilizada), 0)
	into STRICT	qt_saldo_w,
		qt_utilizada_w
	from 	table(pls_conta_autor_pck.obter_dados(	nr_seq_guia_p, 'M', cd_estabelecimento_p,
							null, null, nr_seq_material_w));
		
	if (qt_saldo_w <= 0) then
		ie_retorno_w	:= 'U';
	elsif (qt_saldo_w = qt_autorizada_w) then
		ie_retorno_w	:= 'N';
	else
		ie_retorno_w	:= 'P';
	end if;

end if;

return	ie_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
