CREATE OR REPLACE FUNCTION pls_conta_autor_pck.pls_obter_utiliza_guia ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_tot_utilizado_w	pls_conta_proc.qt_procedimento%type;
qt_tot_autorizado_w	pls_conta_proc.qt_procedimento%type;
nr_seq_guia_principal_w	pls_guia_plano.nr_seq_guia_principal%type;
nr_seq_guia_w		pls_guia_plano.nr_sequencia%type;

BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	max(nr_seq_guia_principal)
	into STRICT	nr_seq_guia_principal_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
	
	if (nr_seq_guia_principal_w IS NOT NULL AND nr_seq_guia_principal_w::text <> '') then
		nr_seq_guia_w	:= nr_seq_guia_principal_w;
	else
		nr_seq_guia_w 	:= nr_seq_guia_p;
	end if;
	
	select	coalesce(sum(qt_utilizada),0),
		coalesce(sum(qt_autorizada),0)
	into STRICT	qt_tot_utilizado_w,
		qt_tot_autorizado_w
	from (	SELECT 	coalesce(sum(qt_utilizada),0) qt_utilizada,
			coalesce(sum(qt_autorizada),0) qt_autorizada
		from 	table(pls_conta_autor_pck.obter_dados(nr_seq_guia_w, 'P', cd_estabelecimento_p))
		
union all

		SELECT 	coalesce(sum(qt_utilizada),0) qt_utilizada,
			coalesce(sum(qt_autorizada),0) qt_autorizada
		from 	table(pls_conta_autor_pck.obter_dados(nr_seq_guia_w, 'M', cd_estabelecimento_p))) alias16;
			
		
	if (qt_tot_utilizado_w = 0) then
		ie_retorno_w	:= 'N';
	elsif (qt_tot_utilizado_w >= qt_tot_autorizado_w) then
		ie_retorno_w	:= 'U';
	else
		ie_retorno_w	:= 'P';
	end if;

end if;

return	ie_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
