CREATE OR REPLACE FUNCTION pls_conta_autor_pck.pls_obter_utiliza_guia_proc ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_guia_proc_p pls_guia_plano_proc.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
qt_autorizada_w		pls_conta_proc.qt_procedimento%type;
qt_utilizada_w		pls_conta_proc.qt_procedimento%type;
qt_saldo_w		pls_conta_proc.qt_procedimento%type;
cd_procedimento_w	pls_conta_proc.cd_procedimento%type;
ie_origem_proced_w	pls_conta_proc.ie_origem_proced%type;


BEGIN

if (nr_seq_guia_proc_p IS NOT NULL AND nr_seq_guia_proc_p::text <> '') and (nr_seq_guia_P IS NOT NULL AND nr_seq_guia_P::text <> '') then

	select	max(cd_procedimento),
		max(ie_origem_proced),
		max(qt_autorizada)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w,
		qt_autorizada_w
	from	pls_guia_plano_proc
	where	nr_sequencia = nr_seq_guia_proc_p;
	
	select 	coalesce(sum(qt_saldo),0),
		coalesce(sum(qt_utilizada),0)
	into STRICT	qt_saldo_w,
		qt_utilizada_w
	from 	table(pls_conta_autor_pck.obter_dados(	nr_seq_guia_p, 'P', cd_estabelecimento_p,
							ie_origem_proced_w, cd_procedimento_w));
		
	if (qt_saldo_w <= 0) then
		ie_retorno_w	:= 'U';
	elsif (qt_saldo_w = qt_autorizada_w) then
		ie_retorno_w	:= 'N';
	else
		ie_retorno_w	:= 'P';
	end if;

end if;

return	ie_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
