CREATE OR REPLACE PROCEDURE pls_contab_onl_lote_fin_pck.verifica_centro_custo ( ie_tipo_centro_custo_p text, nr_seq_plano_p bigint, cd_estabelecimento_p bigint, cd_municipio_ibge_p text, ie_tipo_contratacao_p text, ie_regulamentacao_p text, nr_seq_segurado_p bigint, ie_tipo_item_p text, cd_centro_custo_p INOUT bigint, nr_seq_regra_p INOUT bigint, cd_conta_contabil_p text, nr_seq_prestador_p bigint default null, nr_seq_tipo_lanc_p bigint default null, nr_seq_bonificacao_p bigint default null, nr_seq_sca_p bigint default null) AS $body$
DECLARE


	ie_centro_custo_w       conta_contabil.ie_centro_custo%type;
	cd_centro_custo_fixo_w  conta_contabil.cd_centro_custo%type;
	nr_seq_plano_w          pls_plano.nr_sequencia%type;
	
BEGIN
	select	max(ie_centro_custo),
		max(cd_centro_custo)
	into STRICT	ie_centro_custo_w,
		cd_centro_custo_fixo_w
	from	conta_contabil
	where	cd_conta_contabil	= cd_conta_contabil_p;

	if (ie_centro_custo_w = 'S') then
		if (cd_centro_custo_fixo_w IS NOT NULL AND cd_centro_custo_fixo_w::text <> '') then
			cd_centro_custo_p	:= cd_centro_custo_fixo_w;
		else
			nr_seq_plano_w := nr_seq_plano_p;
			if (coalesce(nr_seq_plano_w::text, '') = '') then
				begin
				select	b.nr_sequencia
				into STRICT	nr_seq_plano_w
				from	pls_segurado a,
						pls_plano b
				where	a.nr_sequencia	= nr_seq_segurado_p
				and	    b.nr_sequencia  = a.nr_seq_plano;
				exception
				when others then
					nr_seq_plano_w	:= null;
				end;
			end if;

			pls_obter_centro_custo(	'D',
						nr_seq_plano_w,
						cd_estabelecimento_p,
						'',
						'',
						ie_regulamentacao_p,
						nr_seq_segurado_p,
						'',
						cd_centro_custo_p,
						nr_seq_regra_p);
		end if;
	end if;
END;

$body$
