CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.alimenta_campo_indent_esp ( tb_nr_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_fornecedor_material_p INOUT pls_util_cta_pck.t_varchar2_table_200, tb_setor_atendimento_p INOUT pls_util_cta_pck.t_varchar2_table_200, tb_seq_prestador_p INOUT pls_util_cta_pck.t_varchar2_table_200) AS $body$
BEGIN

if (tb_nr_seq_item_p.count > 0) then
	
	-- o campo cd_seq_prestador na tabela principal comporta apenas 50 caracteres

	-- aqui _ feito um substr para que n_o seja salvo mais que esta quantidade no campo

	forall i in tb_nr_seq_item_p.first..tb_nr_seq_item_p.last
		update	pls_conta_item_imp_tmp
		set	cd_fornecedor_material = tb_fornecedor_material_p(i),
			cd_setor_atendimento = tb_setor_atendimento_p(i),
			cd_seq_prestador_conv = substr(tb_seq_prestador_p(i), 1, 50)
		where 	nr_sequencia = tb_nr_seq_item_p(i);
	commit;
end if;

tb_nr_seq_item_p.delete;
tb_fornecedor_material_p.delete;
tb_setor_atendimento_p.delete;
tb_seq_prestador_p.delete;

END;

$body$
