--Atualiza os campos necess_rios da tabela CBO_SAUDE



CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.alimenta_campos_cbo_saude () AS $body$
DECLARE


tb_nr_seq_cbo_saude_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cbo_saude
	where	coalesce(ie_situacao::text, '') = '';


BEGIN
--Limpa a tabela

tb_nr_seq_cbo_saude_w.delete;
--Abre o cursor

open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_cbo_saude_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_cbo_saude_w.count = 0;
	
	-- Se encontrou algum registro faz o update

	forall i in tb_nr_seq_cbo_saude_w.first..tb_nr_seq_cbo_saude_w.last
		update 	cbo_saude 
		set	ie_situacao = 'A'
		where	nr_sequencia = tb_nr_seq_cbo_saude_w(i);
	commit;	
end loop;
close C01;
--Limpa a tabela

tb_nr_seq_cbo_saude_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
