CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.alimenta_campos_reg_tab_tiss () AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conversao_tabela_tiss
	where	coalesce(ie_excecao::text, '') = '';
	
C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conversao_tabela_tiss
	where	coalesce(ie_situacao::text, '') = '';


BEGIN
tb_nr_sequencia_w.delete;

open C01;
loop
	fetch C01 bulk collect into tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update	pls_conversao_tabela_tiss
		set	ie_excecao = 'N'
		where  nr_sequencia = tb_nr_sequencia_w(i);
	commit;	
end loop;
close C01;

tb_nr_sequencia_w.delete;

open C02;
loop
	fetch C02 bulk collect into tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update 	pls_conversao_tabela_tiss
		set     ie_situacao = 'I'
		where  	nr_sequencia = tb_nr_sequencia_w(i);
	commit;	
end loop;
close C02;

tb_nr_sequencia_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
