CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.alimenta_tabela_conta_proc_imp ( tb_nr_seq_item_p INOUT pls_util_cta_pck.t_number_table, tb_ie_origem_proced_p INOUT pls_util_cta_pck.t_number_table, tb_cd_procedimento_p INOUT pls_util_cta_pck.t_number_table, tb_nr_regra_origem_p INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN
-- s_ executa se tiver item

if (tb_nr_seq_item_p.count > 0) then
	-- os campos ie_tipo_item_conv, cd_tipo_tabela_conv e ie_tipo_despesa_conv s_o alimentados porque

	
	forall i in tb_nr_seq_item_p.first..tb_nr_seq_item_p.last
		update 	pls_conta_item_imp
		set	ie_origem_proced_conv = tb_ie_origem_proced_p(i),
			cd_procedimento_conv = tb_cd_procedimento_p(i),
			nr_seq_regra_orig_proced_conv = tb_nr_regra_origem_p(i),
			ie_tipo_item_conv = 'P',
			cd_tipo_tabela_conv = cd_tipo_tabela,
			ie_tipo_despesa_conv = ie_tipo_despesa
		where  	nr_sequencia = tb_nr_seq_item_p(i);
	commit;
end if;

tb_nr_seq_item_p.delete;
tb_ie_origem_proced_p.delete;
tb_nr_regra_origem_p.delete;
tb_cd_procedimento_p.delete;

END;

