CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.aplica_conversao_procedimento ( nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


tb_nr_seq_item_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_conta_w		pls_util_cta_pck.t_number_table;
tb_dt_execucao_w		pls_util_cta_pck.t_date_table;
tb_cd_proced_conv_w		pls_util_cta_pck.t_number_table;
tb_ie_origem_proced_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_congen_conv_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_prest_conv_w		pls_util_cta_pck.t_number_table;
tb_cd_tipo_tabela_w		pls_util_cta_pck.t_varchar2_table_10;
tb_ie_tipo_item_conv_w		pls_util_cta_pck.t_varchar2_table_10;
tb_nr_seq_segurado_conv_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_contrato_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_intercambio_w		pls_util_cta_pck.t_number_table;

C01 CURSOR(	nr_seq_protocolo_pc	pls_protocolo_conta_imp.nr_sequencia%type) FOR
	SELECT	c.nr_sequencia nr_seq_item,
		b.nr_sequencia nr_seq_conta,
		c.dt_execucao,
		c.cd_procedimento_conv,
		c.ie_origem_proced_conv,
		b.nr_seq_congenere_conv,
		a.nr_seq_prestador_conv,
		c.cd_tipo_tabela_conv,
		c.ie_tipo_item_conv,
		b.nr_seq_segurado_conv,
		(SELECT w.nr_seq_contrato from pls_segurado w where w.nr_sequencia = b.nr_seq_segurado_conv) nr_seq_contrato,
		(select w.nr_seq_intercambio from pls_segurado w where w.nr_sequencia = b.nr_seq_segurado_conv) nr_seq_intercambio
	from	pls_protocolo_conta_imp a,
		pls_conta_imp b,
		pls_conta_item_imp c
	where	a.nr_sequencia		= nr_seq_protocolo_pc
	and	b.nr_seq_protocolo	= a.nr_sequencia
	and	c.nr_seq_conta		= b.nr_sequencia
	and	c.ie_tipo_item_conv 	= 'P';


BEGIN

-- limpa as vari_veis

tb_nr_seq_item_w.delete;
tb_dt_execucao_w.delete;
tb_cd_proced_conv_w.delete;
tb_ie_origem_proced_w.delete;
tb_nr_seq_congen_conv_w.delete;
tb_nr_seq_prest_conv_w.delete;
tb_cd_tipo_tabela_w.delete;
tb_nr_seq_segurado_conv_w.delete;
tb_nr_seq_contrato_w.delete;
tb_nr_seq_intercambio_w.delete;

EXECUTE 'truncate table pls_conta_item_imp_tmp';

-- alimenta a tabela tempor_ria dos itens com os campos necess_rios para processar as regras

open C01(nr_seq_protocolo_p);
loop
	fetch C01 bulk collect into	tb_nr_seq_item_w, tb_nr_seq_conta_w,
					tb_dt_execucao_w, tb_cd_proced_conv_w,
					tb_ie_origem_proced_w, tb_nr_seq_congen_conv_w, 
					tb_nr_seq_prest_conv_w, tb_cd_tipo_tabela_w,
					tb_ie_tipo_item_conv_w, tb_nr_seq_segurado_conv_w,
					tb_nr_seq_contrato_w, tb_nr_seq_intercambio_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_item_w.count = 0;

	forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last
		
		insert 	into pls_conta_item_imp_tmp(
			nr_seq_protocolo, nr_seq_conta, nr_sequencia, 
			dt_execucao, cd_procedimento_conv, ie_origem_proced_conv, 
			nr_seq_congenere_conv, nr_seq_prest_prot_conv, cd_tipo_tabela_conv,
			ie_tipo_item_conv, nr_seq_segurado, nr_seq_contrato,
			nr_seq_intercambio
		) values (
			nr_seq_protocolo_p, tb_nr_seq_conta_w(i), tb_nr_seq_item_w(i), 
			tb_dt_execucao_w(i), tb_cd_proced_conv_w(i), tb_ie_origem_proced_w(i), 
			tb_nr_seq_congen_conv_w(i), tb_nr_seq_prest_conv_w(i), tb_cd_tipo_tabela_w(i),
			tb_ie_tipo_item_conv_w(i), tb_nr_seq_segurado_conv_w(i), tb_nr_seq_contrato_w(i),
			tb_nr_seq_intercambio_w(i)
		);
	commit;
	
end loop;
close C01;

-- faz a selecao das regras que ser_o processadas

CALL pls_conv_xml_cta_pck.gerencia_sel_regra_proced();
-- processa as regras selecionadas e j_ faz as alteracoes necess_rias na tabela principal

CALL pls_conv_xml_cta_pck.processa_regras_conv_proc(cd_estabelecimento_p);

END;

$body$
