CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.formata_nr_cons_profissional ( nr_conselho_prof_p pls_conta_imp.nr_conselho_prof_exec%type, ie_zero_esquerda_p pls_regra_medico_imp.ie_zero_esquerda%type, nr_seq_regra_p pls_regra_medico_imp.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


nr_conselho_prof_conv_w		pls_conta_imp.nr_cons_prof_exec_conv%type;
ie_zero_esquerda_w		pls_regra_medico_imp.ie_zero_esquerda%type;


BEGIN
-- se foi passado apenas a sequencia da regra busca o valor do zero a esquerda

-- sen_o utiliza o mesmo

if (coalesce(ie_zero_esquerda_p::text, '') = '') then

	select	max(ie_zero_esquerda)
	into STRICT	ie_zero_esquerda_w
	from	pls_regra_medico_imp
	where	nr_sequencia = nr_seq_regra_p;
else
	ie_zero_esquerda_w := ie_zero_esquerda_p;
end if;

-- Verifica se a regra diz que tem zero a esquerda ou n_o

if (ie_zero_esquerda_w = 'S') then
	nr_conselho_prof_conv_w := pls_elimina_carac_espec_zero(nr_conselho_prof_p);
else
	nr_conselho_prof_conv_w := elimina_caractere_especial(nr_conselho_prof_p);
end if;

return	nr_conselho_prof_conv_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
