SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.identificacao_final_mat ( cd_versao_tiss_conv_p pls_protocolo_conta_imp.cd_versao_tiss_conv%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_nr_seq_item_w		pls_util_cta_pck.t_number_table;
tb_ie_tipo_desp_conv_w		pls_util_cta_pck.t_varchar2_table_10;
tb_nr_seq_mat_conv_w		pls_util_cta_pck.t_number_table;
tb_seq_tiss_tabela_conv_w	pls_util_cta_pck.t_number_table;

nr_contador_w		integer;

C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_item,
		a.cd_tipo_tabela_conv,
		a.ie_tipo_despesa_conv,
		a.cd_procedimento,
		a.cd_proced_number_conv,
		a.dt_execucao,
		a.ie_tipo_rel_prest_conv,
		a.nr_seq_material_conv
	from	pls_conta_item_imp_tmp a
	where	a.ie_tipo_item_conv = 'M';

BEGIN
-- limpa as vari_veis

SELECT * FROM pls_conv_xml_cta_pck.atualiza_ident_final_mat(tb_nr_seq_item_w, tb_ie_tipo_desp_conv_w, tb_nr_seq_mat_conv_w, tb_seq_tiss_tabela_conv_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_ie_tipo_desp_conv_w := _ora2pg_r.tb_ie_tipo_desp_conv_p; tb_nr_seq_mat_conv_w := _ora2pg_r.tb_nr_seq_mat_conv_p; tb_seq_tiss_tabela_conv_w := _ora2pg_r.tb_seq_tiss_tabela_conv_p;
nr_contador_w := 0;

for r_c01_w in C01 loop

	tb_nr_seq_item_w(nr_contador_w) := r_c01_w.nr_seq_item;
	
	-- faz toda a identificacao final do material baseado na tabela, tipo despesa e regras

	-- _ passado o material que j_ foi identificado como parametro, dentro da rotina verifica se

	-- encontrou um material caso tenha encontrado devolve o que encontrou, se n_o encontrar nada retorna o

	-- mesmo material passado no parametro

	
	if (r_c01_w.nr_seq_material_conv IS NOT NULL AND r_c01_w.nr_seq_material_conv::text <> '') then
		tb_nr_seq_mat_conv_w(nr_contador_w) := r_c01_w.nr_seq_material_conv;
	else
		tb_nr_seq_mat_conv_w(nr_contador_w) :=	pls_conv_xml_cta_pck.obter_seq_material(	r_c01_w.cd_tipo_tabela_conv, r_c01_w.cd_procedimento,
										r_c01_w.cd_proced_number_conv, r_c01_w.dt_execucao, 
										r_c01_w.ie_tipo_despesa_conv, r_c01_w.ie_tipo_rel_prest_conv,
										r_c01_w.nr_seq_material_conv, cd_versao_tiss_conv_p);
	end if;
				
	-- depois de fazer todas as identificacoes do material, identifica o tipo despesa do material pelo material identificado

	-- caso n_o tenha encontrado nenhum material at_ ent_o apenas _ feito uma convers_o do tipo despesa para o dominio do Tasy

	tb_ie_tipo_desp_conv_w(nr_contador_w) := pls_conv_xml_cta_pck.obter_tipo_desp_material(	tb_nr_seq_mat_conv_w(nr_contador_w), r_c01_w.ie_tipo_despesa_conv,
										cd_versao_tiss_conv_p);
										
	-- Obt_m o a sequ_ncia do TISS TIPO TABELA

	tb_seq_tiss_tabela_conv_w(nr_contador_w) := pls_conv_xml_cta_pck.obter_tipo_tiss_tabela(	r_c01_w.cd_tipo_tabela_conv);
	
	if (nr_contador_w >= pls_util_pck.qt_registro_transacao_w) then

		SELECT * FROM pls_conv_xml_cta_pck.atualiza_ident_final_mat(tb_nr_seq_item_w, tb_ie_tipo_desp_conv_w, tb_nr_seq_mat_conv_w, tb_seq_tiss_tabela_conv_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_ie_tipo_desp_conv_w := _ora2pg_r.tb_ie_tipo_desp_conv_p; tb_nr_seq_mat_conv_w := _ora2pg_r.tb_nr_seq_mat_conv_p; tb_seq_tiss_tabela_conv_w := _ora2pg_r.tb_seq_tiss_tabela_conv_p;
		nr_contador_w := 0;
	else
		nr_contador_w := nr_contador_w + 1;
	end if;
end loop;

-- se sobrou algo manda pro banco

SELECT * FROM pls_conv_xml_cta_pck.atualiza_ident_final_mat(tb_nr_seq_item_w, tb_ie_tipo_desp_conv_w, tb_nr_seq_mat_conv_w, tb_seq_tiss_tabela_conv_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_ie_tipo_desp_conv_w := _ora2pg_r.tb_ie_tipo_desp_conv_p; tb_nr_seq_mat_conv_w := _ora2pg_r.tb_nr_seq_mat_conv_p; tb_seq_tiss_tabela_conv_w := _ora2pg_r.tb_seq_tiss_tabela_conv_p;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
