SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.identificacao_inicial_mat () AS $body$
DECLARE


tb_nr_seq_item_w 	pls_util_cta_pck.t_number_table;
tb_nr_seq_material_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_item,
		pls_util_pck.converte_para_numero(pls_obter_seq_codigo_material(null, cd_procedimento)) nr_seq_material
	from	pls_conta_item_imp_tmp
	where 	ie_tipo_item = 'OD'
	and	ie_tipo_despesa in (1,2,3,8);


BEGIN

tb_nr_seq_item_w.delete;
tb_nr_seq_material_w.delete;

open C01;
loop
	fetch C01 bulk collect into 	tb_nr_seq_item_w, tb_nr_seq_material_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_item_w.count = 0;
	
	-- ie_tipo_item_conv fica M porque o original _ OD conforme o padr_o TISS

	-- no Tasy, vamos trabalhar com P ou M

	forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last 
		update 	pls_conta_item_imp
		set     nr_seq_material_ini_conv = tb_nr_seq_material_w(i),
			ie_tipo_item_conv = 'M',
			cd_tipo_tabela_conv = cd_tipo_tabela,
			ie_tipo_despesa_conv = ie_tipo_despesa
		where	nr_sequencia = tb_nr_seq_item_w(i);
	commit;	
end loop;
close C01;	

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
