--Insere os dados CBO no cadastro do profissional



CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.insere_espec_profissional ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_cbo_saude_p cbo_saude.nr_sequencia%type, cd_versao_tiss_p pls_protocolo_conta_imp.cd_versao_tiss%type, nm_usuario_p text) AS $body$
DECLARE


qt_especialidade_w	integer;
qt_tiss_cbo_saude_w	integer;
cd_especialidade_w	tiss_cbo_saude.cd_especialidade%type;


BEGIN
if ((cd_versao_tiss_p IS NOT NULL AND cd_versao_tiss_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_cbo_saude_p IS NOT NULL AND nr_seq_cbo_saude_p::text <> '')) then

	select 	max(cd_especialidade)
	into STRICT 	cd_especialidade_w
	from 	tiss_cbo_saude
	where 	nr_seq_cbo_saude = nr_seq_cbo_saude_p
	and	ie_versao = cd_versao_tiss_p
	and	coalesce(cd_pessoa_fisica::text, '') = '';
	
	if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then

		select 	count(1)
		into STRICT	qt_especialidade_w
		from 	medico_especialidade a
		where	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	a.cd_especialidade	= cd_especialidade_w;
		
		if (qt_especialidade_w = 0) then
			insert into medico_especialidade(
				cd_pessoa_fisica,
				cd_especialidade,
				dt_atualizacao,
				nm_usuario,
				nr_seq_prioridade)
			values (cd_pessoa_fisica_p,
				cd_especialidade_w,
				clock_timestamp(),
				nm_usuario_p,
				1);
		end if;
		
		select 	count(1)
		into STRICT	qt_tiss_cbo_saude_w
		from 	tiss_cbo_saude a
		where	a.nr_seq_cbo_saude	= nr_seq_cbo_saude_p
		and	a.ie_versao		= cd_versao_tiss_p
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.cd_especialidade	= cd_especialidade_w;

		if (qt_tiss_cbo_saude_w = 0) then		
			insert into tiss_cbo_saude(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_cbo_saude,
				ie_versao,
				cd_pessoa_fisica,
				cd_especialidade,
				cd_convenio)
			values (nextval('tiss_cbo_saude_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_cbo_saude_p,
				cd_versao_tiss_p,
				cd_pessoa_fisica_p,
				cd_especialidade_w,
				null);
		end if;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
