CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_carater_atendimento_tasy ( ie_carater_atendimento_p pls_conta_imp.ie_carater_atendimento%type, cd_versao_tiss_p pls_protocolo_conta_imp.cd_versao_tiss%type) RETURNS varchar AS $body$
DECLARE


ie_carater_atendimento_conv_w		pls_conta_imp.ie_carater_atendimento_conv%type;


BEGIN
-- A vari_vel de retorno inicia com o valor informado no XML

ie_carater_atendimento_conv_w := ie_carater_atendimento_p;

-- Se for vers_o TISS superior a 3.01 _ necess_rio fazer uma convers_o

if (cd_versao_tiss_p >= '3.01.00') then
	
	-- Caso tenha vindo como 1 recebe E e como 2 recebe U 

	-- caso n_o seja nenhuma das duas opcoes retorna como o valor do XML

	if (ie_carater_atendimento_p = '1') then
		ie_carater_atendimento_conv_w := 'E';
	elsif (ie_carater_atendimento_p = '2') then
		ie_carater_atendimento_conv_w := 'U';
	end if;
end if;

return	ie_carater_atendimento_conv_w;

END;

