-- Obt_m a sequencia do CBO



CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_cbo_profissional_tasy ( cd_cbo_saude_prof_p pls_conta_imp.cd_cbo_saude_prof_exec%type) RETURNS bigint AS $body$
DECLARE


nr_seq_cbo_saude_w	cbo_saude.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_cbo_saude_w
from	cbo_saude
where	cd_cbo	= cd_cbo_saude_prof_p
and	ie_situacao = 'A';

-- n_o encontrou um CBO ativo verifica se possui um inativo

if (coalesce(nr_seq_cbo_saude_w::text, '') = '') then
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_cbo_saude_w
	from	cbo_saude
	where	cd_cbo	= cd_cbo_saude_prof_p;	
end if;

return	nr_seq_cbo_saude_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
