CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_cd_cooperativa_cart ( cd_usuario_plano_conv_p pls_conta_imp.cd_usuario_plano_conv%type) RETURNS varchar AS $body$
DECLARE


cd_cooperativa_cart_conv_w pls_conta_imp.cd_cooperativa_cart_conv%type;


BEGIN
-- Diego OS 280609 - Alteracao realizada para que seja possivel obter o n_mero da congenere do benef 

-- independentemente se for a carteirinha antiga ou a nova

if (length(cd_usuario_plano_conv_p) = 17) then
	cd_cooperativa_cart_conv_w := substr(cd_usuario_plano_conv_p, 1, 4);
else
	cd_cooperativa_cart_conv_w := substr(cd_usuario_plano_conv_p, 1, 3);
end if;

return cd_cooperativa_cart_conv_w;

END;

