SET client_encoding TO 'UTF8';


-- Obt_m a sequencia do conselho informado



CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_conselho_profissional ( cd_conselho_prof_p pls_conta_imp.cd_conselho_prof_exec%type, cd_versao_tiss_p pls_protocolo_conta_imp.cd_versao_tiss%type) RETURNS bigint AS $body$
DECLARE


nr_seq_conselho_w		conselho_profissional.nr_sequencia%type;
cd_conselho_prof_conv_w		tiss_conselho_profissional.sg_conselho%type;


BEGIN

if (cd_versao_tiss_p >= '3.01.00') then
	select 	max(sg_conselho)
	into STRICT 	cd_conselho_prof_conv_w
	from 	tiss_conselho_profissional
	where 	cd_conselho = cd_conselho_prof_p;
else 	
	cd_conselho_prof_conv_w := cd_conselho_prof_p;
end if;

cd_conselho_prof_conv_w := upper(cd_conselho_prof_conv_w);

select	max(nr_sequencia)
into STRICT	nr_seq_conselho_w
from	conselho_profissional
where	upper(coalesce(ie_conselho_prof_tiss, sg_conselho)) = cd_conselho_prof_conv_w;

return nr_seq_conselho_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
