CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_dt_atendimento_conta ( nr_seq_conta_p pls_conta_imp.nr_sequencia%type, ie_tipo_guia_p pls_protocolo_conta_imp.ie_tipo_guia%type, dt_alta_p pls_conta_imp.dt_fim_faturamento%type, dt_emissao_p pls_conta_imp.dt_emissao_guia%type) RETURNS timestamp AS $body$
DECLARE

dt_atendimento_conta_w	timestamp;


BEGIN

-- Guia de resumo de internacao

if (ie_tipo_guia_p = '5') then
	dt_atendimento_conta_w := dt_alta_p;

-- Guia de consulta, Guia de SP/SADT, Guia de Honor_rio Individual e Guia de Tratamento Odontol_gico

elsif ( ie_tipo_guia_p in ('3', '4', '6', '11')) then
	
	select	max(dt_execucao)
	into STRICT	dt_atendimento_conta_w
	from	pls_conta_item_imp
	where	nr_seq_conta = nr_seq_conta_p;
end if;

if (coalesce(dt_atendimento_conta_w::text, '') = '') then
	dt_atendimento_conta_w := dt_emissao_p;
end if;
	
return	dt_atendimento_conta_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
