CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_grau_participacao ( cd_grau_partic_p pls_conta_item_equipe_imp.cd_grau_partic%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


nr_seq_grau_partic_w	pls_grau_participacao.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_grau_partic_w
from	pls_grau_participacao
where 	cd_tiss = cd_grau_partic_p
and	cd_estabelecimento = cd_estabelecimento_p
and	ie_situacao = 'A';

-- n_o encontrou tenta ver se existe um inativo

if (coalesce(nr_seq_grau_partic_w::text, '') = '') then
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_grau_partic_w
	from	pls_grau_participacao
	where 	cd_tiss = cd_grau_partic_p
	and	cd_estabelecimento = cd_estabelecimento_p;
	
	-- se ainda n_o encontrou tenta sem considerar o estabelecimento

	if (coalesce(nr_seq_grau_partic_w::text, '') = '') then
		
		select	max(nr_sequencia)
		into STRICT	nr_seq_grau_partic_w
		from	pls_grau_participacao
		where 	cd_tiss = cd_grau_partic_p;
	end if;
end if;

return nr_seq_grau_partic_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
