CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_guia_oper_autorizacao ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, cd_guia_operadora_p pls_conta_imp.cd_guia_operadora%type) RETURNS varchar AS $body$
DECLARE


cd_guia_operadora_conv_w	pls_guia_plano.cd_guia%type;


BEGIN

select	max(cd_guia)
into STRICT	cd_guia_operadora_conv_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

-- se n_o encontrou retorna o valor passado no parametro

if (coalesce(cd_guia_operadora_conv_w::text, '') = '') then

	cd_guia_operadora_conv_w := cd_guia_operadora_p;
end if;

return	cd_guia_operadora_conv_w;

END;

