CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_nr_seq_congenere_cart ( cd_coop_cart_number_conv_p pls_conta_imp.cd_coop_cart_number_conv%type) RETURNS bigint AS $body$
DECLARE


nr_seq_congenere_cart_conv_w pls_conta_imp.nr_seq_congenere_cart_conv%type;


BEGIN
-- se tiver mais de uma congenere vinculada a cooperativa n_o _ poss_vel identificar pela carteirinha

begin
	select	nr_sequencia
	into STRICT	nr_seq_congenere_cart_conv_w
	from	pls_congenere
	where	(cd_cooperativa)::numeric  = cd_coop_cart_number_conv_p;
exception
when others then
	nr_seq_congenere_cart_conv_w := null;
end;

return nr_seq_congenere_cart_conv_w;

END;

