CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_origem_proced_regra ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_procedimento_p pls_conta_item_imp_tmp.cd_procedimento%type, dt_execucao_p pls_conta_item_imp_tmp.dt_execucao%type, ie_verifica_proc_ativo_p text, nr_seq_regra_p out pls_regra_origem_proced.nr_sequencia%type) AS $body$
DECLARE


ie_origem_proced_w	pls_regra_origem_proced.ie_origem_proced%type;
nr_seq_regra_w		pls_regra_origem_proced.nr_sequencia%type;
					

BEGIN

ie_origem_proced_w := null;
nr_seq_regra_w := null;

-- verifica os procedimentos ativos ou todos

if (ie_verifica_proc_ativo_p = 'S') then

	select	max(a.ie_origem_proced),
		max(a.nr_sequencia)
	into STRICT	ie_origem_proced_w,
		nr_seq_regra_w
	from	pls_regra_origem_proced	a
	where	a.ie_acao_regra in ('A', 'R')
	and	dt_execucao_p between a.dt_inicio_vigencia_ref and a.dt_fim_vigencia_ref
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and  	exists (	SELECT	1
			from 	procedimento	x
			where	x.ie_origem_proced = a.ie_origem_proced
			and	x.cd_procedimento = cd_procedimento_p
			and	x.ie_situacao = 'A');
else

	select	max(a.ie_origem_proced),
		max(a.nr_sequencia)
	into STRICT	ie_origem_proced_w,
		nr_seq_regra_w
	from	pls_regra_origem_proced	a
	where	a.ie_acao_regra in ('A', 'R')
	and	dt_execucao_p between a.dt_inicio_vigencia_ref and a.dt_fim_vigencia_ref
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and  	exists (	SELECT	1
			from 	procedimento	x
			where	x.ie_origem_proced = a.ie_origem_proced
			and	x.cd_procedimento = cd_procedimento_p);
end if;

nr_seq_regra_p := nr_seq_regra_w;

return;

END;

$body$
