SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_se_internado ( nr_seq_conta_p pls_conta_imp.nr_sequencia%type, ie_tipo_guia_p pls_protocolo_conta_imp.ie_tipo_guia%type default null, nr_seq_tipo_atend_p pls_tipo_atendimento.nr_sequencia%type default null, ie_tipo_atend_imp_p pls_conta_imp.ie_tipo_atendimento%type default null, ie_regime_atendimento_p pls_conta_imp.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta_imp.ie_saude_ocupacional%type default null, dt_atendimento_p pls_conta_imp.dt_atendimento%type default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
				

BEGIN

ds_retorno_w	:= 'N';

-- Se for resumo de internacao ent_o _ internacao

if (ie_tipo_guia_p = '5') then

	ds_retorno_w	:= 'S';
	
-- Se for SP\SADT tem que ver pelo tipo de atendimento da conta.

elsif (ie_tipo_guia_p	= '4') then
	
	if (nr_seq_tipo_atend_p IS NOT NULL AND nr_seq_tipo_atend_p::text <> '') then
	
		select	coalesce(max(ie_internado), 'N')
		into STRICT	ds_retorno_w
		from	pls_tipo_atendimento
		where	nr_sequencia	= nr_seq_tipo_atend_p;
	end if;
	
	--Se for encontrado algum tipo de atendimento que aponte o atendimento como sendo internacao, entao nem

	--olha mais pelo ie_regime_atendimento e ie_saude_Ocupacional na pls_parametros_tiss. Se nao encontrar

	--nenhuma regra pelo tipo de atendimento que aponte como internacao, entao procurara na pls_parametros tambem.

	if ( ds_retorno_w <> 'S') then
	
		if (ie_regime_atendimento_p IS NOT NULL AND ie_regime_atendimento_p::text <> '') then
		
			--Restringe por ie_internado = S pois o valor default da variavel  eh N e basta achar uma regra cujo ie_internado = S que seja

			--vigente, para ser o suficiente.

			select	coalesce(max(ie_internado), 'N')
			into STRICT	ds_retorno_w
			from	pls_parametros_tiss
			where	ie_regime_atendimento = ie_regime_atendimento_p
			and 	dt_inicio_vigencia <= dt_atendimento_p
			and 	dt_fim_vigencia >= dt_atendimento_p
			and 	ie_internado = 'S'
			and 	ie_situacao = 'A';
			
		end if;		
	end if;
	
	if ( ds_retorno_w <> 'S') then
	
		--Se for encontrado algum regime de atendimento que aponte o atendimento como sendo internacao, entao nem

		--olha mais pelo  ie_saude_Ocupacional na pls_parametros_tiss. Se nao encontrar

		--nenhuma regra pelo tipo de atendimento que aponte como internacao, entao procurara 

		--por regras com campo ie_saude_ocupacional na pls_parametros tambem.

		if (ie_saude_ocupacional_p IS NOT NULL AND ie_saude_ocupacional_p::text <> '') then
		
			--Restringe por ie_internado = S pois o valor default da variavel  eh N e basta achar uma regra cujo ie_internado = S que seja

			--vigente, para ser o suficiente.

			select	coalesce(max(ie_internado), 'N')
			into STRICT	ds_retorno_w
			from	pls_parametros_tiss
			where	ie_saude_ocupacional = ie_saude_ocupacional_p
			and 	dt_inicio_vigencia <= dt_atendimento_p
			and 	dt_fim_vigencia >= dt_atendimento_p
			and 	ie_internado = 'S'
			and 	ie_situacao = 'A';
			
		end if;		
	end if;
	
end if;

if (ds_retorno_w	= 'S') then
	ds_retorno_w	:= 'I';
else
	ds_retorno_w	:= 'A';
end if;

return	ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_conv_xml_cta_pck.obter_se_internado ( nr_seq_conta_p pls_conta_imp.nr_sequencia%type, ie_tipo_guia_p pls_protocolo_conta_imp.ie_tipo_guia%type default null, nr_seq_tipo_atend_p pls_tipo_atendimento.nr_sequencia%type default null, ie_tipo_atend_imp_p pls_conta_imp.ie_tipo_atendimento%type default null, ie_regime_atendimento_p pls_conta_imp.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta_imp.ie_saude_ocupacional%type default null, dt_atendimento_p pls_conta_imp.dt_atendimento%type default null) FROM PUBLIC;
