CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_tecnica_utilizada_conv ( ie_tecnica_utilizada_p pls_conta_item_imp.ie_tecnica_utilizada%type, cd_versao_tiss_p pls_protocolo_conta_imp.cd_versao_tiss_conv%type) RETURNS varchar AS $body$
DECLARE

		
ie_tecnica_utilizada_w 	pls_conta_item_imp.ie_tecnica_utilizada%type;
		

BEGIN
-- caso a vers_o n_o seja superior a 3.01.00 retorna o valor que foi passado no parametro

ie_tecnica_utilizada_w := ie_tecnica_utilizada_p;

-- caso vers_o tiss seja superior a 3.01.00 _ necess_rio fazer uma convers_o no 

-- campo ie_tecnica_utilizada

if (cd_versao_tiss_p >= '3.01.00') and (ie_tecnica_utilizada_w IS NOT NULL AND ie_tecnica_utilizada_w::text <> '') then

	if (ie_tecnica_utilizada_w = '1') then
	
		ie_tecnica_utilizada_w := 'C';
		
	elsif (ie_tecnica_utilizada_w = '2') then
	
		ie_tecnica_utilizada_w := 'V';
		
	elsif (ie_tecnica_utilizada_w = '3') then
	
		ie_tecnica_utilizada_w := 'R';
	end if;
end if;

return ie_tecnica_utilizada_w;

END;

