CREATE OR REPLACE FUNCTION pls_conv_xml_cta_pck.obter_tx_via_acesso_conv ( ie_via_acesso_conv_p pls_conta_item_imp.ie_via_acesso_conv%type) RETURNS bigint AS $body$
DECLARE


tx_via_acesso_w		pls_conta_item_imp.tx_item_via_acesso_conv%type;


BEGIN
-- a taxa base _ 100

tx_via_acesso_w := 100;

-- se a via de acesso _ v_lida ent_o verifica

if (ie_via_acesso_conv_p IS NOT NULL AND ie_via_acesso_conv_p::text <> '') then

	case(ie_via_acesso_conv_p) 
		-- quando via de acesso diferente

		when 'D' then
			tx_via_acesso_w := 70;
			
		-- quando mesma via de acesso

		when 'M' then
			tx_via_acesso_w := 50;
		else
			null;
	end case;
end if;

return tx_via_acesso_w;

END;

