CREATE OR REPLACE PROCEDURE pls_conv_xml_cta_pck.pls_conv_nv_gerar_face_dente ( nr_seq_protocolo_p pls_protocolo_conta_imp.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


_ora2pg_r RECORD;
tb_nr_seq_item_w	pls_util_cta_pck.t_number_table;
tb_cd_face_dente_w	pls_util_cta_pck.t_varchar2_table_20;
nr_contador_w		integer;

C01 CURSOR(	nr_seq_protocolo_pc	pls_protocolo_conta_imp.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia nr_seq_item,
		b.cd_face_dente
	from	pls_conta_imp a,
		pls_conta_item_imp b
	where	a.nr_seq_protocolo = nr_seq_protocolo_pc
	and	b.nr_seq_conta = a.nr_sequencia
	and	b.ie_tipo_item_conv = 'P';

BEGIN
-- chamado para limpar as vari_veis

SELECT * FROM pls_conv_xml_cta_pck.atualiza_item_face_dente(tb_nr_seq_item_w, tb_cd_face_dente_w, nm_usuario_p) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_cd_face_dente_w := _ora2pg_r.tb_cd_face_dente_p;
nr_contador_w := 0;

-- percorre todos os procedimentos do protocolo

for r_c01_w in C01(nr_seq_protocolo_p) loop

	-- se algum deles tiver informacao no campo cd_face_dente gera os registros na tabela

	if (r_c01_w.cd_face_dente IS NOT NULL AND r_c01_w.cd_face_dente::text <> '') then

		-- por padr_o TISS existe apenas um campo para informar as faces do dente

		-- percorremos este campo e para cada caracter informado _ gerado um registro 

		-- na tabela pls_item_face_dente_imp

		for i in 0..(length(r_c01_w.cd_face_dente) - 1) loop
			
			tb_cd_face_dente_w(nr_contador_w) := substr(r_c01_w.cd_face_dente, i, 1);
			tb_nr_seq_item_w(nr_contador_w) := r_c01_w.nr_seq_item;
			
			-- se atingiu a quantidade manda pro banco, sen_o incrementa o contador

			if (nr_contador_w >= pls_util_pck.qt_registro_transacao_w) then
			
				SELECT * FROM pls_conv_xml_cta_pck.atualiza_item_face_dente(tb_nr_seq_item_w, tb_cd_face_dente_w, nm_usuario_p) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_cd_face_dente_w := _ora2pg_r.tb_cd_face_dente_p;
				nr_contador_w := 0;
			else
				nr_contador_w := nr_contador_w + 1;
			end if;
		end loop;
	end if;
end loop;

-- se sobrou algo manda pro banco

SELECT * FROM pls_conv_xml_cta_pck.atualiza_item_face_dente(tb_nr_seq_item_w, tb_cd_face_dente_w, nm_usuario_p) INTO STRICT _ora2pg_r;
 tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_item_p; tb_cd_face_dente_w := _ora2pg_r.tb_cd_face_dente_p;

END;

$body$
