SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_coparticipacao_pck.consistir_apropriacoes_regra ( regra_copartic_p pls_regra_copartic) AS $body$
DECLARE

					 
		nr_seq_contr_interc_prod_w	bigint;
		ie_origem_regra_w		varchar(255);
		ds_pasta_w			varchar(255);
		vl_unitario_w			double precision;
		ie_proc_mat_w			varchar(255);
		nr_seq_item_w			bigint;
		vl_fixo_total_w			pls_regra_copartic_aprop.vl_apropriacao%type;
	
BEGIN
		if (regra_copartic_p.nr_seq_contrato IS NOT NULL AND regra_copartic_p.nr_seq_contrato::text <> '') then 
			nr_seq_contr_interc_prod_w := current_setting('pls_coparticipacao_pck.pls_contrato_w')::pls_contrato%rowtype.nr_contrato;
			ie_origem_regra_w	:= 'contrato';
			ds_pasta_w		:= 'OPS - Gestão de Contratos > Contrato > Regras > Coparticipação';
		elsif (regra_copartic_p.nr_seq_intercambio IS NOT NULL AND regra_copartic_p.nr_seq_intercambio::text <> '') then 
			nr_seq_contr_interc_prod_w := regra_copartic_p.nr_seq_intercambio;
			ie_origem_regra_w	:= 'intercâmbio';
			ds_pasta_w		:= 'OPS - Contratos de Intercâmbio > Intercâmbio > Regras > Coparticipação';
		elsif (regra_copartic_p.nr_seq_plano IS NOT NULL AND regra_copartic_p.nr_seq_plano::text <> '') then 
			nr_seq_contr_interc_prod_w := regra_copartic_p.nr_seq_plano;
			ie_origem_regra_w	:= 'produto';
			ds_pasta_w		:= 'OPS - Cadastro de Produtos > Regras > Coparticipação';
		end if;
		 
		if (current_setting('pls_coparticipacao_pck.ie_gerando_conta_proc_mat_w')::varchar(255) = 'P') then 
			vl_unitario_w	:= current_setting('pls_coparticipacao_pck.pls_conta_proc_w')::pls_conta_proc%rowtype.vl_unitario;
			ie_proc_mat_w	:= 'procedimento';
			nr_seq_item_w	:= current_setting('pls_coparticipacao_pck.pls_conta_proc_w')::pls_conta_proc%rowtype.nr_sequencia;
		elsif (current_setting('pls_coparticipacao_pck.ie_gerando_conta_proc_mat_w')::varchar(255) = 'M') then 
			vl_unitario_w	:= current_setting('pls_coparticipacao_pck.pls_conta_mat_w')::pls_conta_mat%rowtype.vl_unitario;
			ie_proc_mat_w	:= 'material';
			nr_seq_item_w	:= current_setting('pls_coparticipacao_pck.pls_conta_mat_w')::pls_conta_mat%rowtype.nr_sequencia;
		end if;
 
		select	sum(vl_apropriacao) 
		into STRICT	vl_fixo_total_w 
		from	pls_regra_copartic_aprop 
		where	nr_seq_regra		= regra_copartic_p.nr_sequencia 
		and	ie_tipo_apropriacao	= '1';
		 
		--As regras de apropriação do tipo "Percentual" e "Percentual sobre a diferença" tem seu percentual consistido no momento da liberação da regra. 
		 
		if (vl_fixo_total_w > vl_unitario_w) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(306782, 
				'NR_SEQ_REGRA_COPARTIC=' || regra_copartic_p.nr_sequencia || 
				';IE_ORIGEM_REGRA=' || ie_origem_regra_w || 
				';NR_SEQ_CONTR_INTERC_PROD=' || nr_seq_contr_interc_prod_w || 
				';PASTA=' || ds_pasta_w || 
				';IE_PROC_MAT=' || ie_proc_mat_w || 
				';NR_SEQ_ITEM=' || nr_seq_item_w || 
				';NR_SEQ_CONTA=' || current_setting('pls_coparticipacao_pck.pls_conta_w')::pls_conta%rowtype.nr_sequencia);
		end if;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
