CREATE OR REPLACE PROCEDURE pls_coparticipacao_pck.consistir_vl_maximo_copartic (nr_seq_centro_apropriacao_p pls_centro_apropriacao.nr_sequencia%type) AS $body$
BEGIN
						if (regras_coparticipacao_w.vl_maximo_copartic IS NOT NULL AND regras_coparticipacao_w.vl_maximo_copartic::text <> '') then 
							if (current_setting('pls_coparticipacao_pck.vl_total_coparticipacao_w')::pls_conta_coparticipacao.vl_coparticipacao%type < regras_coparticipacao_w.vl_maximo_copartic) then 
								if (current_setting('pls_coparticipacao_pck.vl_total_coparticipacao_w')::pls_conta_coparticipacao.vl_coparticipacao%type + vl_apropriado_w <= regras_coparticipacao_w.vl_maximo_copartic) then 
									PERFORM set_config('pls_coparticipacao_pck.vl_apropriado_copartic_w', vl_apropriado_w, false);
									PERFORM set_config('pls_coparticipacao_pck.vl_total_coparticipacao_w', current_setting('pls_coparticipacao_pck.vl_total_coparticipacao_w')::pls_conta_coparticipacao.vl_coparticipacao%type + vl_apropriado_w, false);
								else 
									PERFORM set_config('pls_coparticipacao_pck.vl_apropriado_copartic_w', regras_coparticipacao_w.vl_maximo_copartic - current_setting('pls_coparticipacao_pck.vl_total_coparticipacao_w')::pls_conta_coparticipacao.vl_coparticipacao%type, false);
									PERFORM set_config('pls_coparticipacao_pck.vl_total_coparticipacao_w', regras_coparticipacao_w.vl_maximo_copartic, false);
								end if;
							else 
								PERFORM set_config('pls_coparticipacao_pck.vl_apropriado_copartic_w', 0, false);
							end if;
						else 
							PERFORM set_config('pls_coparticipacao_pck.vl_apropriado_copartic_w', vl_apropriado_w, false);
							PERFORM set_config('pls_coparticipacao_pck.vl_total_coparticipacao_w', current_setting('pls_coparticipacao_pck.vl_total_coparticipacao_w')::pls_conta_coparticipacao.vl_coparticipacao%type + vl_apropriado_w, false);
						end if;
						 
						if (current_setting('pls_coparticipacao_pck.vl_apropriado_copartic_w')::pls_conta_copartic_aprop.vl_apropriacao%type > 0) then 
							insert into pls_conta_copartic_aprop( 
								nr_sequencia, 
								nr_seq_conta_coparticipacao, 
								nr_seq_centro_apropriacao, 
								vl_apropriacao, 
								nm_usuario, 
								nm_usuario_nrec, 
								dt_atualizacao, 
								dt_atualizacao_nrec) 
							values (	nextval('pls_conta_copartic_aprop_seq'), 
								current_setting('pls_coparticipacao_pck.nr_seq_conta_coparticipacao_w')::pls_conta_coparticipacao.nr_sequencia%type, 
								nr_seq_centro_apropriacao_p, 
								current_setting('pls_coparticipacao_pck.vl_apropriado_copartic_w')::pls_conta_copartic_aprop.vl_apropriacao%type, 
								current_setting('pls_coparticipacao_pck.nm_usuario_w')::usuario.nm_usuario%type, 
								current_setting('pls_coparticipacao_pck.nm_usuario_w')::usuario.nm_usuario%type, 
								clock_timestamp(), 
								clock_timestamp());
						end if;
					END;

$body$
