CREATE OR REPLACE FUNCTION pls_coparticipacao_pck.get_ie_status_mensalidade () RETURNS varchar AS $body$
DECLARE

		ie_cobrar_copartic_w	pls_requisicao.ie_cobrar_copartic%type;
		current_setting('pls_coparticipacao_pck.ie_status_mensalidade_w')::varchar(1)	varchar(1);
	
BEGIN
		select	max(b.ie_cobrar_copartic) 
		into STRICT	ie_cobrar_copartic_w 
		from	pls_execucao_req_item	a, 
			pls_requisicao		b 
		where	a.nr_seq_requisicao	= b.nr_sequencia 
		and	a.nr_seq_guia		= current_setting('pls_coparticipacao_pck.pls_conta_w')::pls_conta%rowtype.nr_seq_guia;
 
		if (coalesce(ie_cobrar_copartic_w,'S') = 'N') then 
			PERFORM set_config('pls_coparticipacao_pck.ie_status_mensalidade_w', 'C', false);
		else 
			if (coalesce(current_setting('pls_coparticipacao_pck.pls_parametros_w')::pls_parametros%rowtype.ie_controle_coparticipacao,'N') = 'S') then 
				PERFORM set_config('pls_coparticipacao_pck.ie_status_mensalidade_w', 'P', false);
			else 
				if (get_ie_status_item in ('L','S')) then 
					PERFORM set_config('pls_coparticipacao_pck.ie_status_mensalidade_w', 'L', false);
				else 
					PERFORM set_config('pls_coparticipacao_pck.ie_status_mensalidade_w', 'P', false);
				end if;
			end if;
		end if;
		 
		return current_setting('pls_coparticipacao_pck.ie_status_mensalidade_w')::varchar(1);
	END;

$body$
