CREATE OR REPLACE FUNCTION pls_coparticipacao_pck.get_qt_itens_cobrar (pls_regra_copartic_p pls_regra_copartic) RETURNS bigint AS $body$
DECLARE

		 
		regras_incidencia CURSOR FOR 
			SELECT	* 
			from	pls_regra_copartic_util 
			where	nr_seq_regra = pls_regra_copartic_p.nr_sequencia;
			
		current_setting('pls_coparticipacao_pck.qt_itens_cobrar_w')::bigint	bigint;
	
BEGIN
		if (pls_regra_copartic_p.ie_utilizacao = 'S') then 
			for regras_incidencia_w in regras_incidencia loop 
				if (current_setting('pls_coparticipacao_pck.ie_gerando_conta_proc_mat_w')::varchar(255) = 'P') and 
				  ((regras_incidencia_w.ie_eventos_incidencia = 'S') or (pls_se_grupo_preco_servico_lib(regras_incidencia_w.nr_seq_grupo_serv, current_setting('pls_coparticipacao_pck.pls_conta_proc_w')::pls_conta_proc%rowtype.cd_procedimento, current_setting('pls_coparticipacao_pck.pls_conta_proc_w')::pls_conta_proc%rowtype.ie_origem_proced) = 'S')) then 
					 
					PERFORM set_config('pls_coparticipacao_pck.qt_itens_cobrar_w', coalesce(current_setting('pls_coparticipacao_pck.qt_itens_cobrar_w')::bigint, 0) + pls_coparticipacao_pck.get_qt_proc_incidencia_cobrar(regras_incidencia_w, pls_regra_copartic_p), false);
					 
				elsif (current_setting('pls_coparticipacao_pck.ie_gerando_conta_proc_mat_w')::varchar(255) = 'M' and regras_incidencia_w.ie_eventos_incidencia = 'S') then 
 
					PERFORM set_config('pls_coparticipacao_pck.qt_itens_cobrar_w', coalesce(current_setting('pls_coparticipacao_pck.qt_itens_cobrar_w')::bigint, 0) + pls_coparticipacao_pck.get_qt_mat_incidencia_cobrar(regras_incidencia_w, pls_regra_copartic_p), false);	
					 
				end if;
			end loop;
		end if;
		 
		return coalesce(current_setting('pls_coparticipacao_pck.qt_itens_cobrar_w')::bigint, get_qt_itens);
	END;

$body$
LANGUAGE PLPGSQL
