CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.enviar_solicitacao ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

ie_status_w		pls_creden_prestador.ie_status%type;
cd_estabelecimento_w	pls_creden_prestador.cd_estabelecimento%type;
qt_inconsistencia_w	integer;

BEGIN

select	count(1)
into STRICT	qt_inconsistencia_w
from	table(validar_credenciamento(nr_seq_credenciamento_p));

if (qt_inconsistencia_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1202672); --A solicitacao nao pode ser enviada porque ha inconsistencia(s).
else
	select	ie_status,
		cd_estabelecimento
	into STRICT	ie_status_w,
		cd_estabelecimento_w
	from	pls_creden_prestador
	where	nr_sequencia	= nr_seq_credenciamento_p;
	
	update	pls_creden_prestador
	set	ie_status	= 2,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_credenciamento_p;
	
	if (ie_status_w = '1') then
		CALL pls_creden_prestador_pck.gerar_chamado(	nr_seq_credenciamento_p	=> nr_seq_credenciamento_p,
				nm_usuario_p		=> nm_usuario_p,
				cd_estabelecimento_p	=> cd_estabelecimento_w );
		
		CALL CALL pls_creden_prestador_pck.gerar_alerta_evento(	nr_seq_credenciamento_p => nr_seq_credenciamento_p,
					ie_tipo_processo_p => 5,
					nm_usuario_p => nm_usuario_p,
					ie_commit_p => 'S' );
	else
		update	pls_cred_prest_revisao
		set	dt_liberacao		= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_credenciamento	= nr_seq_credenciamento_p
		and	coalesce(dt_liberacao::text, '') = '';
	end if;
	
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
