CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_anexos ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_seq_prest_tipo_doc_w		pls_prestador_tipo_doc.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	ds_documento,
		nr_seq_tipo_documento,
		dt_emissao,
		dt_validade
	from	pls_cred_prest_anexo
	where	nr_seq_credenciamento	= nr_seq_credenciamento_p;

BEGIN
for c01_w in c01 loop
	begin
	insert	into	pls_prestador_tipo_doc(	nr_sequencia, nr_seq_prestador, nm_usuario,
			dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec,
			dt_inicio_vigencia, nr_seq_tipo_documento, dt_fim_vigencia )
		values (nextval('pls_prestador_tipo_doc_seq'), nr_seq_prestador_p, nm_usuario_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			coalesce(c01_w.dt_emissao,clock_timestamp()), c01_w.nr_seq_tipo_documento, c01_w.dt_validade )
		return;
	
	insert	into	pls_prestador_documento(	nr_sequencia, nr_seq_prest_tipo, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			ds_arquivo, dt_documento, dt_aceite,
			ie_status )
		values (nextval('pls_prestador_documento_seq'), nr_seq_prest_tipo_doc_w, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			c01_w.ds_documento, coalesce(c01_w.dt_emissao,clock_timestamp()), clock_timestamp(),
			'A' );
	end;
end loop;
END;

$body$
