CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_chamado ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nm_usuario_p wsuite_usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

nr_seq_tipo_chamado_w		pls_param_cred_prestador.nr_seq_tipo_chamado%type;
ds_tipo_credenciamento_w	pls_tipo_cred_prestador.ds_tipo_credenciamento%type;
nr_seq_wsuite_usuario_w		wsuite_usuario.nr_sequencia%type;
ds_razao_social_w		pls_creden_prestador.ds_razao_social%type;


BEGIN

select	max(nr_seq_tipo_chamado)
into STRICT	nr_seq_tipo_chamado_w
from	pls_param_cred_prestador
where	cd_estabelecimento = cd_estabelecimento_p;

if (nr_seq_tipo_chamado_w IS NOT NULL AND nr_seq_tipo_chamado_w::text <> '') then
	begin
	select	a.nr_seq_wsuite_usuario,
		a.ds_razao_social,
		b.ds_tipo_credenciamento
	into STRICT	nr_seq_wsuite_usuario_w,
		ds_razao_social_w,
		ds_tipo_credenciamento_w
	from	pls_creden_prestador a,
		pls_tipo_cred_prestador b
	where	b.nr_sequencia	= a.nr_seq_tipo_credenciamento
	and	a.nr_sequencia	= nr_seq_credenciamento_p;
	exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1207549); --Registro invalido
	end;

	insert	into	pls_chamado(	nr_sequencia, nr_seq_tipo_chamado, cd_estabelecimento,
			ie_tipo_atendimento, dt_abertura, ds_assunto,
			ds_detalhamento, ie_status, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nr_seq_wsuite_usuario, nr_seq_credenciamento )
		values (nextval('pls_chamado_seq'), nr_seq_tipo_chamado_w, cd_estabelecimento_p,
			'CP', clock_timestamp(), substr(wheb_mensagem_pck.get_texto(1202309,'DS_TIPO_CREDENCIAMENTO='||ds_tipo_credenciamento_w),1,80),
			substr(wheb_mensagem_pck.get_texto(1202306,'NR_SEQ_CREDENCIAMENTO='||nr_seq_credenciamento_p||';DS_RAZAO_SOCIAL='||ds_razao_social_w),1,80), 1, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_wsuite_usuario_w, nr_seq_credenciamento_p );
end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
