CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_colaboradores ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	nm_pessoa,
		nr_seq_tipo_vinculo,
		ds_email,
		(ds_email ||' / '||nr_ddi_telefone||' '||nr_telefone) ds_observacao
	from	pls_cred_prest_colaborador
	where	nr_seq_credenciamento	= nr_seq_credenciamento_p;
BEGIN
for c01_w in c01 loop
	begin
	insert	into	pls_prestador_colaborador(	nr_sequencia, nr_seq_prestador, dt_atualizacao_nrec,
			nm_usuario_nrec, dt_atualizacao, nm_usuario,
			nm_colaborador, nr_seq_tipo_vinc, ds_observacao )
		values (nextval('pls_prestador_colaborador_seq'), nr_seq_prestador_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			substr(c01_w.nm_pessoa,1,60), c01_w.nr_seq_tipo_vinculo, c01_w.ds_observacao );
	end;
end loop;
END;

$body$
