CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_credenciamento ( nr_seq_wsuite_usuario_p pls_creden_prestador.nr_seq_wsuite_usuario%type, nr_seq_tipo_credenciamento_p pls_creden_prestador.nr_seq_tipo_credenciamento%type, cd_cnes_p pls_creden_prestador.cd_cnes%type, cd_estabelecimento_p pls_creden_prestador.cd_estabelecimento%type, nm_usuario_p pls_creden_prestador.nm_usuario%type, ie_tipo_solicitante_p pls_creden_prestador.ie_tipo_solicitante%type, nr_seq_credenciamento_p INOUT pls_creden_prestador.nr_sequencia%type) AS $body$
DECLARE

nr_seq_prestador_w	pls_creden_prestador.nr_seq_prestador%type;
qt_prestador_w		integer;

BEGIN
select	count(1)
into STRICT	qt_prestador_w
from	pessoa_juridica pj,
	pls_prestador p
where	p.cd_cgc = pj.cd_cgc
and	pj.cd_cnes = cd_cnes_p;

if (qt_prestador_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1205765);
else
	select	max(cp.nr_sequencia)
	into STRICT	nr_seq_prestador_w
	from	pls_creden_prestador cp
	where	cp.ie_status <> '6'
	and	cp.cd_cnes = cd_cnes_p;
	
	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1205767);
	else

		insert	into	pls_creden_prestador(	nr_sequencia, nr_seq_wsuite_usuario, dt_atualizacao_nrec,
				nm_usuario_nrec, dt_atualizacao, nm_usuario,
				nr_seq_tipo_credenciamento, cd_cnes, cd_estabelecimento,
				ie_status, ie_tipo_solicitante )
			values (nextval('pls_creden_prestador_seq'), nr_seq_wsuite_usuario_p, clock_timestamp(),
				nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_tipo_credenciamento_p, cd_cnes_p, cd_estabelecimento_p,
				'7', ie_tipo_solicitante_p )
			returning nr_sequencia into nr_seq_credenciamento_p;

		CALL grava_dados_api_cnes_pck.consultar_cnes(	cd_cnes_p		=> cd_cnes_p,
								ie_origem_consulta_p	=> 1,
								nm_usuario_p		=> nm_usuario_p );

		CALL CALL pls_creden_prestador_pck.gerar_alerta_evento(	nr_seq_credenciamento_p => nr_seq_credenciamento_p,
					ie_tipo_processo_p => 7,
					nm_usuario_p => nm_usuario_p,
					ie_commit_p => 'S');
		commit;
	end if;
end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
