CREATE OR REPLACE PROCEDURE pls_creden_prestador_pck.gerar_especialidades ( nr_seq_credenciamento_p pls_creden_prestador.nr_sequencia%type, nr_seq_prestador_p pls_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	nr_seq_credenciamento,
		cd_especialidade,
		ie_especialidade_princ,
		nr_seq_area_atuacao
	from	pls_cred_prest_med_espec
	where	nr_seq_credenciamento	= nr_seq_credenciamento_p
	and	coalesce(nr_seq_cred_prest_med::text, '') = '';
BEGIN
for c01_w in c01 loop
	begin
	insert	into	pls_prestador_med_espec(	nr_sequencia, nr_seq_prestador, nm_usuario,
			dt_atualizacao, nm_usuario_nrec, dt_atualizacao_nrec,
			cd_especialidade, nr_seq_area_atuacao, ie_especialidade_princ,
			ie_rce_atuacao, ie_guia_medico, ie_ptu,
			ie_endereco_principal )
		values (nextval('pls_prestador_med_espec_seq'), nr_seq_prestador_p, nm_usuario_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			c01_w.cd_especialidade, c01_w.nr_seq_area_atuacao, c01_w.ie_especialidade_princ, 
			'N', 'N', 'N',
			'S' );
	end;
end loop;
END;

